<template>
  <div >
    <div class="form-container">
      <el-form label-position="left" :model="ruleForm" :rules="rules" ref="ruleForm" label-width="100px" class="demo-ruleForm login-container">
        <h3 class="title">注册</h3>
        <el-form-item label="手机号" prop="phone">
          <el-input type="text" v-model="ruleForm.phone" autocomplete="off"></el-input>
        </el-form-item>
        <el-form-item label="密码" prop="pass">
          <el-input type="password" v-model="ruleForm.pass" autocomplete="off"></el-input>
        </el-form-item>
        <el-form-item label="确认密码" prop="checkPass">
          <el-input type="password" v-model="ruleForm.checkPass" autocomplete="off"></el-input>
        </el-form-item>
        <el-form-item>
          <el-button type="primary" @click="submitForm('ruleForm')">提交</el-button>
          <el-button @click="resetForm('ruleForm')">重置</el-button>
        </el-form-item>
      </el-form>
    </div>
  </div>
</template>

<script>
  import { setCookie, getCookie, delCookie,Encrypt,Decrypt,delData } from '../utils/util'
  export default {
    name: 'register',
    data() {
      var phoneValidate1 = (rule, value, callback) => {
        if (value === '') {
          callback(new Error('请输入手机号'));
        } else {
          if (this.ruleForm.checkPass !== '') {
            this.$refs.ruleForm.validateField('checkPass');
          }
          callback();
        }
      };
      var phoneValidate2 = (rule, value, callback) => {
        if (value.length != 11) {
          callback(new Error('请输入正确的手机号'));
        } else {
          if (this.ruleForm.checkPass !== '') {
            this.$refs.ruleForm.validateField('checkPass');
          }
          callback();
        }
      };
      var validatePass1 = (rule, value, callback) => {
        if (value === '') {
          callback(new Error('请输入密码'));
        } else {
          if (this.ruleForm.checkPass !== '') {
            this.$refs.ruleForm.validateField('checkPass');
          }
          callback();
        }
      };
      var validatePass2 = (rule, value, callback) => {
        if (value.length < 8 || value.length > 16) {
          callback(new Error('密码必须在8-16位之间'));
        } else {
          if (this.ruleForm.checkPass !== '') {
            this.$refs.ruleForm.validateField('checkPass');
          }
          callback();
        }
      };
      var validatePass3 = (rule, value, callback) => {
        if (value === '') {
          callback(new Error('请再次输入密码'));
        } else if (value !== this.ruleForm.pass) {
          callback(new Error('两次输入密码不一致!'));
        } else {
          callback();
        }
      };
      return {
        ruleForm: {
          phone: '',
          pass: '',
          checkPass: '',
        },
        rules: {
          phone: [
            { validator: phoneValidate1, trigger: 'blur' },
            { validator: phoneValidate2, trigger: 'change' },
          ],
          pass: [
            { validator: validatePass1, trigger: 'blur' },
            { validator: validatePass2, trigger: 'change' }
          ],
          checkPass: [
            { validator: validatePass3, trigger: 'blur' }
          ],
        }
      };
    },
    methods: {
      submitForm(formName) {
        this.$refs[formName].validate((valid) => {
          if (valid) {
            console.log(this.ruleForm.phone)
            let pwd = Encrypt(this.ruleForm.pass)
            let user = {name: this.ruleForm.phone, password: pwd}
            this.$axios.post("http://zhsdsb.top:8888/users/signup", user).then(response => {
              if (response.data.success){
                this.$message.success("注册成功")
                setTimeout(() => {
                  this.$router.push({path : "/"})
                },1000)
              } else {
                this.$message.error(response.data.name)
              }
            }).catch(function (error) {
              console.log(error)
            })
          } else {
            console.log('error submit!!');
            return false;
          }
        });
      },
      resetForm(formName) {
        this.$refs[formName].resetFields();
      }
    }
  }
</script>

